<?phprequire_once __DIR__ . "/../classes/display/displayTime.php";require_once __DIR__ . "/../classes/backend.php";//create new backend object, if not included//(then $backend is probably created and we can reuse it)if (!isset($backend)) {    $backend = new Backend();}//Time$time = new DisplayTime();$time->setUnsafeStartDate(isset($_GET['year']) ? $_GET['year'] : null, isset($_GET['month']) ? $_GET['month'] : null, isset($_GET['day']) ? $_GET['day'] : null);$backend->setTime($time);//little handy trick to chose the right oneswitch(true){    case isset($_GET['day']):        $backend->setModus(Backend::MODUS_DAY);        break;    case isset($_GET['month']):        $backend->setModus(Backend::MODUS_MONTH);        break;    case isset($_GET['year']):        $backend->setModus(Backend::MODUS_YEAR);        break;}//Force modus if given correctif (isset($_GET['modus'])) {    $backend->setUnsafeModus($_GET['modus']);}//Lengthif (isset($_GET['length'])) {    $backend->setUnsafeListLength($_GET['length']);}//Offset (not used yet)if (isset($_GET['offset'])) {    $backend->setUnsafeOffset($_GET['offset']);}//Force Refreshif (isset($_GET['refresh'])) {    $backend->setUnsafeForceRefresh($_GET['refresh']);}//list for all.php (default all)if (isset($_GET['list'])) {    $backend->setUnsafeShowList($_GET['list']);}//set filter double bot (if given)if(isset($_GET['doubleFilter'])){    $backend->setFilterDoubleBot(true);}?>