<?php	//modus Day/Month/Year (0/1/2)	if(!isset($fstat_backend_modus)){		$fstat_backend_modus = 1;//default is month overview	}		//Time	if(!isset($fstat_backend_year)){		if(isset($_GET['year'])){			$fstat_backend_year = preg_replace('#[^0-9]#i','',$_GET['year']);//alles ausser 0-9 mit nichts ersetzten			$fstat_backend_modus = 2;		}else{			$fstat_backend_year = gmdate("Y");		}	}		if(!isset($fstat_backend_month)){		if(isset($_GET['month'])){			$fstat_backend_month = preg_replace('#[^0-9]#i','',$_GET['month']);//alles ausser 0-9 mit nichts ersetzten			if(($fstat_backend_month > 12) || ($fstat_backend_month == 0)){				$fstat_backend_month = gmdate("n");			}			$fstat_backend_modus = 1;		}else{			$fstat_backend_month = gmdate("n");		}	}		if(!isset($fstat_backend_day)){		if(isset($_GET['day'])){			$fstat_backend_day = preg_replace('#[^0-9]#i','',$_GET['day']);//alles ausser 0-9 mit nichts ersetzten			if(($fstat_backend_day > 31) || ($fstat_backend_day == 0)){				$fstat_backend_day = gmdate("j");			}			$fstat_backend_modus = 0;		}else{			$fstat_backend_day = gmdate("j");		}	}		//Force modus if given correct	if(isset($_GET['modus'])){		$tmp = $fstat_backend_modus;		$fstat_backend_modus = preg_replace('#[^0-9]#i','',$_GET['modus']);//alles ausser 0-9 mit nichts ersetzten		if($fstat_backend_modus > 2){$fstat_backend_modus = $tmp;}	}			//Offset and Length	if(!isset($fstat_backend_offset)){		if(isset($_GET['offset'])){			$fstat_backend_offset = preg_replace('#[^0-9]#i','',$_GET['offset']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_offset = 0;		}	}		if(!isset($fstat_backend_length)){		if(isset($_GET['length'])){			$fstat_backend_length = preg_replace('#[^0-9]#i','',$_GET['length']);//alles ausser 0-9 mit nichts ersetzten			if($fstat_backend_length > 200){$fstat_backend_length = 200;}//Schutz vor zu langen Listen		}else{			$fstat_backend_length = $fstat_last_length;		}	}		//list for all.php (default all)	if(!isset($fstat_backend_get_list)){		if(isset($_GET['list'])){			$fstat_backend_get_list = preg_replace('#[^0-9a-z|]#i','',$_GET['list']);//alles ausser 0-9 und A-Z mit nichts ersetzten			if($fstat_backend_get_list == ""){				$fstat_backend_get_list = "all";			}		}else{			$fstat_backend_get_list = "all";		}	}		//Force Refresh	if(!isset($fstat_backend_refresh)){		if(isset($_GET['refresh'])){			$fstat_backend_refresh = preg_replace('#[^0-9]#i','',$_GET['refresh']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_refresh = 0; //default is use cache / not forced refresh		}	}?>