<?php		//Time	if(!isset($fstat_backend_month)){		if(isset($_GET['month'])){			$fstat_backend_month = preg_replace('#[^0-9]#i','',$_GET['month']);//alles ausser 0-9 mit nichts ersetzten			if(($fstat_backend_month > 12) || ($fstat_backend_month == 0)){				$fstat_backend_month = gmdate("m");			}		}else{			$fstat_backend_month = gmdate("m");		}	}		if(!isset($fstat_backend_year)){		if(isset($_GET['year'])){			$fstat_backend_year = preg_replace('#[^0-9]#i','',$_GET['year']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_year = gmdate("Y");		}	}		if(!isset($fstat_backend_timestamp)){		$fstat_backend_timestamp = gmmktime(1, 1, 1, $fstat_backend_month, 1, $fstat_backend_year);	}		//Start/End Timestamp	if(!isset($fstat_backend_start_timestamp)){		if(isset($_GET['start'])){			$fstat_backend_start_timestamp = preg_replace('#[^0-9]#i','',$_GET['start']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_start_timestamp = gmmktime(1, 1, 1, $fstat_backend_month, 1, $fstat_backend_year);		}	}		if(!isset($fstat_backend_end_timestamp)){		if(isset($_GET['end'])){			$fstat_backend_end_timestamp = preg_replace('#[^0-9]#i','',$_GET['end']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_end_timestamp = gmmktime(1, 1, 1, $fstat_backend_month, gmdate("t", $fstat_backend_timestamp), $fstat_backend_year);		}	}		if($fstat_backend_start_timestamp > $fstat_backend_end_timestamp){//switching if end before start		$tmp = $fstat_backend_start_timestamp;		$fstat_backend_start_timestamp = $fstat_backend_end_timestamp;		$fstat_backend_end_timestamp = $tmp;	}			//Offset and Length	if(!isset($fstat_backend_offset)){		if(isset($_GET['offset'])){			$fstat_backend_offset = preg_replace('#[^0-9]#i','',$_GET['offset']);//alles ausser 0-9 mit nichts ersetzten		}else{			$fstat_backend_offset = 0;		}	}		if(!isset($fstat_backend_length)){		if(isset($_GET['length'])){			$fstat_backend_length = preg_replace('#[^0-9]#i','',$_GET['length']);//alles ausser 0-9 mit nichts ersetzten			if($fstat_backend_length > 200){$fstat_backend_length = 200;}//Schutz vor zu langen Listen		}else{			$fstat_backend_length = $fstat_last_length;		}	}?>